$(document).ready(function () {
   
  $.post("./data.php",
          {},
          function(data) {
            var i;
            for (i=0; i < data.length; i++) {
              console.log(data[i].sourcename);
              $("#picture_" + i + " img").attr("src", "./images/carousel/" + data[i].sourcename);
              $("#picture_" + i + " h6").text(data[i].photoname);
              $("#picture_" + i + " p").text(data[i].photo_info);            
            }
          },
          "json");  
});